  "experiment_config": {
    "version": "7.3",
    "name": "speaker_character_pattern_analysis",
    "description": "This experiment tests the Character Assessment Framework's ability to detect \nmeaningful differences in character patterns between individual speakers. \nThe analysis focuses on three core objectives: speaker differentiation using \nthe 10 CAF dimensions, identification of unique character signatures across \nthe 5 virtues and 5 vices, and examination of MC-SCI character coherence patterns.\n",
    "hypothesis": "The Character Assessment Framework will successfully differentiate between \nspeakers using the 10 character dimensions, reveal distinct character signatures \nfor each speaker across virtues and vices, and demonstrate meaningful variation \nin MC-SCI character coherence patterns.\n",
    "framework": "../../frameworks/reference/core/caf_v7.3.md",
    "corpus_path": "corpus/",
    "analysis": {
      "evaluations_per_document": 1
    },
    "synthesis": {
      "model": "vertex_ai/gemini-2.5-flash-lite"
    },
    "validation": {
      "required_tests": [
        "speaker_differentiation_anova",
        "character_signature_analysis",
        "mc_sci_coherence_patterns"
      ],
      "reliability_threshold": 0.7,
      "effect_size_reporting": true
    },
    "reporting": {
      "format": "academic",
      "structure": [
        "executive_summary",
        "speaker_differentiation_results",
        "character_signature_analysis",
        "mc_sci_coherence_patterns",
        "qualitative_insights",
        "statistical_methodology",
        "framework_validation",
        "limitations"
      ],
      "show_statistical_work": true,
      "include_confidence_metrics": true
    },
    "hypotheses": {
      "H1_Speaker_Differentiation": "The 10 CAF dimensions will show statistically significant differences between speakers",
      "H2_Character_Signatures": "Each speaker will exhibit a unique character signature across the 5 virtues and 5 vices",
      "H3_MC_SCI_Patterns": "MC-SCI scores will vary meaningfully between speakers, indicating different levels of character coherence"
    },
    "workflow": [
      {
        "agent": "EnhancedAnalysisAgent",
        "inputs": [
          "experiment",
          "framework",
          "corpus"
        ],
        "outputs": [
          "raw_analysis_log"
        ]
      },
      {
        "agent": "IntelligentExtractorAgent",
        "inputs": [
          "raw_analysis_log",
          "framework"
        ],
        "outputs": [
          "structured_data"
        ]
      },
