  "experiment_config": {
    "version": "7.0",
    "name": "populist_discourse_factorial_analysis",
    "description": "This experiment analyzes discourse patterns in presidential speeches using the \nPopulist Discourse Analysis Framework (PDAF) v7.1. The corpus employs a \ntemporal design examining presidential speeches across multiple contexts \nspanning 2017-2025. The analysis will measure populist discourse dimensions \nto identify any statistical patterns, relationships, or temporal trends \npresent in the data.\n",
    "hypothesis": "Populist discourse dimensions will be analyzed across presidential speeches \nto test for statistical patterns and relationships. The analysis will examine \nwhether temporal factors, speech context factors, or their interaction account \nfor variance in populist discourse patterns. Statistical tests will determine \nif significant relationships exist between experimental factors and Populist \nStrategic Contradiction Index scores.\n",
    "framework": "../../frameworks/reference/flagship/pdaf_v7.3.md",
    "corpus_path": "corpus/",
    "models": [
      "vertex_ai/gemini-2.5-pro"
    ],
    "runs_per_model": 3,
    "analysis_variant": "default",
    "analysis": {
      "evaluations_per_document": 3,
      "statistical_confidence": 0.95,
      "variance_threshold": 0.15
    },
    "validation": {
      "required_tests": [
        "temporal_pattern_analysis",
        "populist_dimension_correlation_matrix",
        "psci_reliability",
        "anchor_clustering_analysis",
        "speech_context_analysis",
        "tension_pattern_analysis"
      ],
      "reliability_threshold": 0.7,
      "effect_size_reporting": true
    },
    "reporting": {
      "format": "academic",
      "structure": [
        "executive_summary",
        "populist_discourse_patterns",
        "temporal_factor_analysis",
        "anchor_dimension_analysis",
        "strategic_tension_relationships",
        "speech_context_patterns",
        "reliability_assessment",
        "statistical_methodology",
        "findings_discussion",
        "limitations"
      ],
      "show_statistical_work": true,
      "include_confidence_metrics": true
    },
    "hypotheses": {
      "H1_Temporal": "Test whether populist discourse dimensions differ significantly across time periods",
      "H2_Context": "Test whether populist discourse dimensions differ significantly between speech contexts",
      "H3_Relationships": "Test whether populist anchor dimensions show significant correlational patterns",
      "H4_Strategic_Tensions": "Test whether strategic tension measures correlate with populist intensity",
      "H5_Index_Patterns": "Test whether PSCI scores show systematic patterns across temporal factors",
      "H6_Platform_Validation": "Test whether the JSON synthesis architecture successfully processes populist discourse factorial design with 9-dimensional analysis"
    },
    "workflow": [
      {
        "agent": "EnhancedAnalysisAgent",
        "inputs": [
          "experiment",
