        },
        "strategic_patterns": {
          "coherent_populist_strategy": {
            "condition": "overall_populism_index > 1.0 AND populist_strategic_contradiction_index < 0.3",
            "description": "Consistent populist approach across dimensions"
          },
          "strategic_populist_contradiction": {
            "condition": "populist_strategic_contradiction_index > 0.5",
            "description": "Deliberate deployment of contradictory populist appeals"
          },
          "left_wing_populism": {
            "condition": "economic_populist_appeals > 1.0 AND nationalist_exclusion < 0.5",
            "description": "Redistributive populist focus"
          },
          "right_wing_populism": {
            "condition": "nationalist_exclusion > 1.0 AND economic_populist_appeals < nationalist_exclusion",
