import pandas as pd
import numpy as np
from scipy import stats
from itertools import combinations
import json # Used for pretty printing the final output, not strictly required for the analysis itself.

def perform_statistical_analysis(statistical_data):
    """
    Performs raw aggregation and pairwise independent t-tests on the provided data.

    Args:
        statistical_data (dict): A nested dictionary where keys are filenames,
                                 values are dictionaries of model names, and
                                 their values are lists of analysis results for each run.
                                 Example:
                                 {
                                   "filename.md": {
                                     "model_A": [result1, result2, result3],
                                     "model_B": [result4, result5, result6]
                                   }
                                 }

    Returns:
        dict: A dictionary containing the results of the statistical analyses.
              Structure:
              {
                "raw_aggregation": {
                  "model_A": {"count": N, "mean": M, "std": S, "min": Min, "max": Max},
                  "model_B": {...}
                },
                "t_test": {
                  "model_A_vs_model_B": {"statistic": T, "pvalue": P},
                  "model_A_vs_model_C": {...}
                }
              }
    """

    statistical_results = {}

    # Step 1: Data Preprocessing - Flatten into a Pandas DataFrame
    # This step collects all valid numerical results for each model into a single structure.
    flat_data_list = []
    for filename, models_results in statistical_data.items():
        for model_name, results_list in models_results.items():
            # Filter out None values and ensure data points are numerical (int or float)
            for res in results_list:
                if res is not None and (isinstance(res, (int, float))):
                    flat_data_list.append({"model": model_name, "result": res})

    # Create a DataFrame from the flattened data
    df = pd.DataFrame(flat_data_list)

    # Step 2: raw_aggregation
    raw_aggregation_results = {}
    if not df.empty:
        # Group by model and aggregate common descriptive statistics
        # std: Using ddof=1 for sample standard deviation (unbiased estimator).
        #      Handles cases where there's only one sample by returning NaN (which converts to None).
        raw_aggregation_df = df.groupby('model')['result'].agg(
            count='count',
            mean='mean',
            std=('result', lambda x: x.std(ddof=1) if len(x) > 1 else np.nan),
            min='min',
            max='max'
        )

        # Convert the DataFrame to a dictionary, handling NaN values by converting them to None
        # for better JSON serialization and general Python compatibility.
        raw_aggregation_results = raw_aggregation_df.applymap(lambda x: x if not pd.isna(x) else None).to_dict('index')

        # Ensure count is an integer and other stats are floats (or None)
        for model, stats_dict in raw_aggregation_results.items():
            stats_dict['count'] = int(stats_dict['count'])
            for key in ['mean', 'std', 'min', 'max']:
                if stats_dict[key] is not None:
                    stats_dict[key] = float(stats_dict[key])
    else:
        raw_aggregation_results = {} # If no valid data, the aggregation result is empty

    statistical_results["raw_aggregation"] = raw_aggregation_results

    # Step 3: t_test (Independent T-test for all pairs of models)
    t_test_results = {}

    # Get unique model names from the DataFrame to iterate through all possible pairs
    model_names = df['model'].unique().tolist() if not df.empty else []

    # Generate all unique pairs of models for pairwise comparison
    for model1, model2 in combinations(model_names, 2):
        # Extract data for the current pair of models
        data1 = df[df['model'] == model1]['result'].tolist()
        data2 = df[df['model'] == model2]['result'].tolist()

        # A t-test requires at least 2 samples per group to calculate variance.
        if len(data1) >= 2 and len(data2) >= 2: 
            # Perform independent t-test using Welch's t-test (equal_var=False)
            # Welch's t-test is generally preferred as it does not assume equal variances
            # between the groups, making it more robust.
            t_statistic, p_value = stats.ttest_ind(data1, data2, equal_var=False) 

            t_test_results[f"{model1}_vs_{model2}"] = {
                "statistic": float(t_statistic), # Convert NumPy float to standard Python float
                "pvalue": float(p_value)         # Convert NumPy float to standard Python float
            }
        else:
            # If insufficient data, record None for statistic and p-value, along with a note
            t_test_results[f"{model1}_vs_{model2}"] = {
                "statistic": None,
                "pvalue": None,
                "note": (f"Insufficient data (model '{model1}' has {len(data1)} samples, "
                         f"model '{model2}' has {len(data2)} samples) to perform t-test. "
                         "At least 2 samples required per group.")
            }

    statistical_results["t_test"] = t_test_results

    return statistical_results

# Example Usage with the provided data structure:
statistical_data = {
  "sanitized_speech_a1c5e7d2.md": {
    "claude-3-5-sonnet-20240620": [10.5, 11.0, 9.8],
    "gpt-4o": [12.1, 11.5, 12.8],
    "claude-3-haiku-20240307": [8.0, 8.5, 7.9]
  },
  "sanitized_speech_f9b8c2d7.md": {
    "claude-3-5-sonnet-20240620": [10.2, 10.7, 10.0],
    "gpt-4o": [11.9, 12.3, 11.7],
    "claude-3-haiku-20240307": [8.3, 8.1, 8.4]
  },
  "sanitized_speech_c3e8a1f4.md": {
    "claude-3-5-sonnet-20240620": [11.0, None, 10.5], # Example with a None value to demonstrate cleaning
    "gpt-4o": [12.5, 12.0, 12.7],
    "claude-3-haiku-20240307": [8.6, 8.2, 8.0]
  },
  "sanitized_speech_d9e2b0a1.md": { 
    "claude-3-5-sonnet-20240620": [9.5, 9.7, 9.3],
    "gpt-4o": [None, None, None], # Example with all null results for a model
    "claude-3-haiku-20240307": [7.0, 7.5, 7.2]
  },
  "sanitized_speech_e1f3g4h5.md": { 
    "claude-3-5-sonnet-20240620": [10.1, 10.3, None],
    "gpt-4o": [12.0, None, None], # Example with a model having only one valid data point after cleaning
    "claude-3-haiku-20240307": [7.8, 7.9, 7.7]
  }
}

# Perform the analysis
statistical_results = perform_statistical_analysis(statistical_data)

# Print the results for verification (using json.dumps for pretty printing)
# Note: numpy.nan values are converted to None during the process for JSON compatibility.
print(json.dumps(statistical_results, indent=2))
