def preprocess_data(data):
    """
    Parses and normalizes the raw analysis data into two DataFrames:
    one for dimensional scores and one for derived metrics.
    Handles both simple and complex (raw_score, salience) formats.
    """
    processed = {}
    for entry in data:
        doc_idx = entry['document_index']
        if doc_idx not in processed:
            processed[doc_idx] = {}

        if entry['data_type'] == 'scores':
            flat_scores = {}
            for key, value in entry['scores'].items():
                if isinstance(value, dict):
                    flat_scores[f'{key}_raw_score'] = value.get('raw_score')
                    flat_scores[f'{key}_salience'] = value.get('salience')
                    flat_scores[f'{key}_confidence'] = value.get('confidence')
                else:
                    # Handle simple format, assume it's raw_score
                    flat_scores[f'{key}_raw_score'] = value
                    flat_scores[f'{key}_salience'] = np.nan # Salience is unknown
                    flat_scores[f'{key}_confidence'] = np.nan # Confidence is unknown
            processed[doc_idx].update(flat_scores)

        elif entry['data_type'] == 'derived_metrics':
            processed[doc_idx].update(entry['scores'])

    # Convert to DataFrame
    full_df = pd.DataFrame.from_dict(processed, orient='index')

    # Split into scores and metrics
    score_cols = [c for c in full_df.columns if '_raw_score' in c or '_salience' in c or '_confidence' in c]
    metric_cols = [c for c in full_df.columns if c not in score_cols]

    scores_df = full_df[score_cols]
    # For cleaner analysis, let's focus on raw_scores
    raw_scores_df = scores_df[[c for c in scores_df.columns if '_raw_score' in c]]
    raw_scores_df.columns = [c.replace('_raw_score', '') for c in raw_scores_df.columns]

    metrics_df = full_df[metric_cols]

    return raw_scores_df, metrics_df, full_df

# 1. Data Preprocessing
