# --- Analysis Functions ---

def display_descriptive_stats(df, title):
    """Calculates and prints descriptive statistics for a DataFrame."""
    print(f"--- Descriptive Statistics: {title} ---\n")
    if df.empty:
        print("No data available for this category.\n")
        return
    # Use display to ensure nice formatting in notebooks
    from IPython.display import display
    display(df.describe().T)
    print("\n")

def plot_correlation_heatmap(df, title):
    """Plots a correlation heatmap for a DataFrame."""
    if df.empty or df.shape[1] < 2:
        print(f"Not enough data to plot correlation heatmap for {title}.\n")
        return

    plt.figure(figsize=(12, 10))
    corr = df.corr()
    mask = np.triu(np.ones_like(corr, dtype=bool))
    sns.heatmap(corr, mask=mask, annot=True, fmt=".2f", cmap='coolwarm',
                vmin=-1, vmax=1, linewidths=.5)
    plt.title(f'Correlation Heatmap: {title}', fontsize=16)
    plt.xticks(rotation=45, ha='right')
    plt.yticks(rotation=0)
    plt.tight_layout()
    plt.show()

def perform_group_comparison(df, group_col, target_cols, p_threshold=0.05):
    """
    Performs independent t-tests between two groups for a list of target columns.
    """
    print(f"--- Group Comparison T-Tests (Comparing by '{group_col}') ---\n")

    if group_col not in df.columns:
        print(f"Grouping column '{group_col}' not found.")
        return

    df['group'] = np.where(df[group_col] >= 0, 'Cohesive', 'Fragmentative')

    group1 = df[df['group'] == 'Cohesive']
    group2 = df[df['group'] == 'Fragmentative']

    if len(group1) < 2 or len(group2) < 2:
        print("Not enough data in one or both groups to perform t-tests.")
        return

    results = []
    for col in target_cols:
        stat, p_val = ttest_ind(group1[col], group2[col], nan_policy='omit', equal_var=False) # Welch's t-test
        results.append({
            'Dimension': col,
            'Mean (Cohesive)': group1[col].mean(),
            'Mean (Fragmentative)': group2[col].mean(),
            'T-statistic': stat,
            'P-value': p_val,
            'Significant': 'Yes' if p_val < p_threshold else 'No'
        })

    results_df = pd.DataFrame(results).set_index('Dimension')
    from IPython.display import display
    display(results_df.sort_values('P-value'))


