    "derived_metrics": [
        {
            "name": "net_sentiment",
            "formula": "dimensions.positive_sentiment.raw_score - dimensions.negative_sentiment.raw_score"
        },
        {
            "name": "sentiment_magnitude",
            "formula": "(dimensions.positive_sentiment.raw_score + dimensions.negative_sentiment.raw_score) / 2"
        }
    ]
}

results = {}

for doc_id, scores in document_scores.items():
    derived_metrics_results = {}
    positive_sentiment_score = scores["positive_sentiment"]["raw_score"]
    negative_sentiment_score = scores["negative_sentiment"]["raw_score"]

    for metric in framework_definition["derived_metrics"]:
        formula = metric["formula"]
        # Safely evaluate the formula using a dictionary with defined variables
        try:
            # Using eval with a limited scope for security
            # We are passing the raw scores directly as variables
            metric_value = eval(formula, {"dimensions": {"positive_sentiment": {"raw_score": positive_sentiment_score}, "negative_sentiment": {"raw_score": negative_sentiment_score}}})
            derived_metrics_results[metric["name"]] = metric_value
        except Exception as e:
            derived_metrics_results[metric["name"]] = f"Error calculating metric: {e}"

    results[doc_id] = derived_metrics_results

print(json.dumps(results, indent=2))
