import json

# Document scores
document_scores = {
  "document_0": {
    "positive_sentiment": {
      "raw_score": 1.0,
      "salience": 1.0,
      "confidence": 1.0
    },
    "negative_sentiment": {
      "raw_score": 0.0,
      "salience": 0.0,
      "confidence": 1.0
    }
  }
}

# Derived metrics calculation
results = {}
for doc_id, scores in document_scores.items():
    positive_sentiment = scores["positive_sentiment"]["raw_score"]
    negative_sentiment = scores["negative_sentiment"]["raw_score"]

    # Net Sentiment
    net_sentiment = positive_sentiment - negative_sentiment

    # Sentiment Magnitude
    sentiment_magnitude = (positive_sentiment + negative_sentiment) / 2

    results[doc_id] = {
        "net_sentiment": net_sentiment,
        "sentiment_magnitude": sentiment_magnitude
    }

print(json.dumps(results, indent=2))
