import json

# Document scores provided
document_scores = {
  "document_0": {
    "positive_sentiment": {
      "raw_score": 1.0,
      "salience": 1.0,
      "confidence": 1.0
    },
    "negative_sentiment": {
      "raw_score": 0.0,
      "salience": 0.0,
      "confidence": 1.0
    }
  }
}

# Framework definitions for derived metrics
framework_definitions = {
  "derived_metrics": [
    {
      "name": "net_sentiment",
      "formula": "dimensions.positive_sentiment.raw_score - dimensions.negative_sentiment.raw_score"
    },
    {
      "name": "sentiment_magnitude",
      "formula": "(dimensions.positive_sentiment.raw_score + dimensions.negative_sentiment.raw_score) / 2"
    }
  ]
}

# Calculate derived metrics for document_0
document_id = "document_0"
scores = document_scores[document_id]

results = {}

for metric in framework_definitions["derived_metrics"]:
    formula = metric["formula"]
    # Replace dimension names with their actual values from the scores
    calculated_value = eval(formula.replace("dimensions.positive_sentiment.raw_score", str(scores["positive_sentiment"]["raw_score"])).replace("dimensions.negative_sentiment.raw_score", str(scores["negative_sentiment"]["raw_score"])))
    results[metric["name"]] = calculated_value

# Add the original dimensional scores to the results
results["dimensional_scores"] = scores

print(json.dumps({document_id: results}, indent=2))
