import json

# Input scores for document_1
document_scores = {
  "document_1": {
    "positive_sentiment": {
      "raw_score": 1.0,
      "salience": 1.0,
      "confidence": 1.0
    },
    "negative_sentiment": {
      "raw_score": 0.0,
      "salience": 0.0,
      "confidence": 1.0
    }
  }
}

# Framework definitions for derived metrics
framework_definitions = {
    "derived_metrics": [
        {
            "name": "net_sentiment",
            "formula": "dimensions.positive_sentiment.raw_score - dimensions.negative_sentiment.raw_score"
        },
        {
            "name": "sentiment_magnitude",
            "formula": "(dimensions.positive_sentiment.raw_score + dimensions.negative_sentiment.raw_score) / 2"
        }
    ]
}

results = {}

# Iterate through each document
for doc_id, scores in document_scores.items():
    derived_metrics_results = {}
    positive_sentiment_score = scores["positive_sentiment"]["raw_score"]
    negative_sentiment_score = scores["negative_sentiment"]["raw_score"]

    # Calculate derived metrics based on the framework
    for metric in framework_definitions["derived_metrics"]:
        formula = metric["formula"]
        # Safely evaluate the formula using eval() with a restricted environment
        try:
            # Define local variables for eval to access
            local_vars = {
                "dimensions": {
                    "positive_sentiment": {"raw_score": positive_sentiment_score},
                    "negative_sentiment": {"raw_score": negative_sentiment_score}
                }
            }
            # Execute the formula
            derived_score = eval(formula, {"__builtins__": None}, local_vars)
            derived_metrics_results[metric["name"]] = derived_score
        except Exception as e:
            derived_metrics_results[metric["name"]] = f"Error calculating metric: {e}"

    results[doc_id] = {
        "dimensional_scores": scores,
        "derived_metrics": derived_metrics_results
    }

# Print the results
print(json.dumps(results, indent=2))
