import json

# Provided scores for document_3
document_scores = [
  {
    "document_id": "document_3",
    "positive_sentiment": {
      "raw_score": 0.0,
      "salience": 0.0,
      "confidence": 1.0
    },
    "negative_sentiment": {
      "raw_score": 1.0,
      "salience": 1.0,
      "confidence": 1.0
    }
  }
]

results = []

for doc_data in document_scores:
    positive_sentiment = doc_data["positive_sentiment"]["raw_score"]
    negative_sentiment = doc_data["negative_sentiment"]["raw_score"]

    # Calculate derived metrics
    net_sentiment = positive_sentiment - negative_sentiment
    sentiment_magnitude = (positive_sentiment + negative_sentiment) / 2

    # Construct the output for this document
    derived_metrics_output = {
        "document_id": doc_data["document_id"],
        "derived_metrics": {
            "net_sentiment": net_sentiment,
            "sentiment_magnitude": sentiment_magnitude
        },
        "dimensional_scores": doc_data # Include original dimensional scores
    }
    results.append(derived_metrics_output)

print(json.dumps(results, indent=2))
