import json

# Document scores
document_scores = {
  "document_id": "document_3",
  "positive_sentiment": {
    "raw_score": 0.0,
    "salience": 0.0,
    "confidence": 1.0
  },
  "negative_sentiment": {
    "raw_score": 1.0,
    "salience": 1.0,
    "confidence": 1.0
  }
}

# Framework definitions for derived metrics
framework_config = {
    "derived_metrics": [
        {
            "name": "net_sentiment",
            "formula": "dimensions.positive_sentiment.raw_score - dimensions.negative_sentiment.raw_score"
        },
        {
            "name": "sentiment_magnitude",
            "formula": "(dimensions.positive_sentiment.raw_score + dimensions.negative_sentiment.raw_score) / 2"
        }
    ]
}

# Calculate derived metrics
derived_metrics_results = {}

for metric in framework_config["derived_metrics"]:
    formula = metric["formula"]
    # Replace dimension names with their raw scores from the document
    calculated_value = eval(formula.replace("dimensions.positive_sentiment.raw_score", str(document_scores["positive_sentiment"]["raw_score"]))
                                   .replace("dimensions.negative_sentiment.raw_score", str(document_scores["negative_sentiment"]["raw_score"])))
    derived_metrics_results[metric["name"]] = calculated_value

# Combine original scores with derived metrics
output_data = document_scores.copy()
output_data["derived_metrics"] = derived_metrics_results

# Print the results
print(json.dumps(output_data, indent=2))
